mapper.update:
update student set name=#{name}, gender=#{gender}, clazz_id=#{clazzId},
                   degree=#{degree},no=#{no},phone=#{phone},is_college=#{isCollege},
                   address=#{address},graduation_date=#{graduationDate},id_card=#{idCard},update_time=#{updateTime},
                   violation_score=#{violationScore},violation_count=#{violationCount}
                   where id=#{id}

mapper.select:
select s.*,clazz.name clazzName from student s left join clazz on s.clazz_id = clazz.id
and clazz_id = #{clazzId}
and name like concat('%', #{name}, '%')
and degree = #{degree}
order by update_time desc
